mapper.select:
select l.lbmno, l.mno, l.lbm_nm, l.phot, l.cdt, l.mdt, (l.opn ='u') opn, sr.cnt as srCnt from mv_lbm l inner join mv_memb m on m.mno = l.mno left join (SELECT l.lbmno, count(s.srno) as cnt from mv_lbm l, mv_lbm_sr s where l.lbmno = s.lbmno group by lbmno) as sr on l.lbmno = sr.lbmno where l.mno = #{mno}
and l.opn ='u'
order by cdt desc
limit #{paging.startRowNo} , #{paging.pageSize}

        select phot = (select phot from mv_sr where srno=#{srno})
		from mv_lbm
		where lbmno = #{lbmno}

mapper.delete:
DELETE 
FROM mv_lbm_sr 
WHERE lbmno = #{lbmno} and srno = #{srno}

mapper.update:
UPDATE mv_lbm
   SET phot = null
 WHERE lbmno = #{lbmno}
