mapper.select:
SELECT srno, mvno, mno, titl, time, phot, cont, spo
  FROM mv_sr
 WHERE srno = #{value}

SELECT count(cmno)
  FROM mv_sr_cmt
 WHERE srno = #{srno}

mapper.update:
UPDATE mv_sr_cmt
   SET cont = #{cont}
 WHERE cmno = #{cmno}

mapper.delete:
DELETE FROM mv_lbm_sr
 WHERE lbmno = #{lbmno}
   AND srno = #{srno}

mapper.update:
UPDATE IGNORE mv_sr
SET mno=21
WHERE mno=#{mno}
